jan_2019_nm &lt;- nm_combined |&gt;
  filter(mon == 1) |&gt;
  pull(`2019`)

nm_combined_no_jan &lt;- nm_combined |&gt;
  filter(mon != 1)

nm_2019_min &lt;- nm_combined_no_jan$`2019` |&gt;
  min()

nm_2019_max &lt;- nm_combined_no_jan$`2019` |&gt;
